mapper.insert:
 	insert into tb_member
  	 (
	   	 mem_seq
	   	 ,mem_name
	   	 ,mem_password
	   	 ,mem_email
	   	 ,mem_nickname
	   	 ,mem_wdate
	   	 ,mem_img
	   	 ,mem_imgsize
  	 )
  	 values
  	 (
  	  	SQ_MEMBER.nextval
	   	 ,#{mem_name}
	   	 ,#{mem_password}
	   	 ,#{mem_email}
	   	 ,#{mem_nickname}
	   	 ,sysdate
	   	 ,#{mem_img}
	   	 ,#{mem_imgsize}

  	 )

mapper.update:
update tb_member
SET mem_nickname = #{mem_nickname}	
,mem_email = #{mem_email}
,mem_password = #{mem_password}
,mem_img = #{mem_img}
,mem_imgsize = #{mem_imgsize}
WHERE mem_name = #{mem_name}
